// 1000 decimals of pi
const pi_str = "3.";

// Handle input
const input = document.getElementById("input");
const output = document.getElementById("output");

input.addEventListener("input", (e) => {
	let val = e.target.value.trim();

	if (isNaN(val)) {
		output.innerHTML = "Input is not a number";
		output.className = "";
		return;
	}

	if (val.length == 0) {
		output.innerHTML = "Start typing..."
		output.className = "";
	} else if (val.length <= pi_str.length) {
		let pi_cmp = pi_str.substring(0, val.length);
		if (pi_cmp === val) {
			let num_decimals = Math.max(0, val.length - 2);
			output.innerHTML = `Correct (${num_decimals} decimals)`;
			output.className = "correct";
		} else {
			output.innerHTML = "Incorrect";
			output.className = "incorrect";
		}
	} else {
		output.innerHTML = "Input is too long";
		output.className = "";
	}
});

// Disable pasting and dragging
input.addEventListener("paste", (e) => { e.preventDefault(); alert("No copy-pasting!") });
input.addEventListener("drop", (e) => { e.preventDefault(); });
